import { useEffect, useState } from "react"

function StudentList(){
    const [data, setData] = useState(null)
    const [value, setValue] = useState("")
    useEffect(() => {
        loadData()
    },[])

    const loadData = () => {
        fetch("https://kinara-test-api.onrender.com/student")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                console.log(resp)
                setData(resp)
            })
    }

//serach functionality
const SearchData = (e) => {
    console.log(e.target.value)
    setValue(e.target.value)
}
const handleSubmit = async (e) => {
    e.preventDefault()
    return await axios.get(`https://web-api-level2-algomax.onrender.com/users?q=${value}`)
        .then((res) => {
            setData(res.data)
            setValue("")
        })
        .catch((err) => {
            console.log(err)
        })
}
// reset functinality
const reset = (e) => {
    e.preventDefault()
    fetch("https://web-api-level2-algomax.onrender.com/users")
        .then((res) => {
            return res.json()
        })
        .then((resp) => {
            setData(resp)
        })
}

    return(
        <div className="container">
            <div className="card">
                <div className="card-title">
                    Student Data
                </div>
                <div className="card-body">
                <table className="table table-bordred">
                        <thead className="bg-dark text-white">
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Total Marks</th>
                                <th>Mobile No</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data?.map((item) => (
                                <tr key={item.id}>
                                    <td>{item.id}</td>
                                    <td>{item.name}</td>
                                    <td>{item.totalmarks}</td>
                                    <td>{item.mobile}</td>
                                    <td>
                                        <a className="btn btn-danger">Delete</a>
                                        <a  className="btn btn-primary" style={{ marginLeft: "10px" }}>Edit</a>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
            
        </div>
    )
}

export default StudentList;
